import{_ as t,o as e,c as d,V as l}from"./chunks/framework.ced88878.js";const u=JSON.parse('{"title":"编译选项","description":"","frontmatter":{},"headers":[],"relativePath":"zh/tsconfig-reference/project-config/Compiler Options.md","lastUpdated":1682668835000}'),o={name:"zh/tsconfig-reference/project-config/Compiler Options.md"},c=l('<h1 id="编译选项" tabindex="-1">编译选项 <a class="header-anchor" href="#编译选项" aria-label="Permalink to &quot;编译选项&quot;">​</a></h1><h2 id="编译选项-1" tabindex="-1">编译选项 <a class="header-anchor" href="#编译选项-1" aria-label="Permalink to &quot;编译选项&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">选项</th><th style="text-align:left;">类型</th><th style="text-align:left;">默认值</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;"><code>--allowJs</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">允许编译javascript文件。</td></tr><tr><td style="text-align:left;"><code>--allowSyntheticDefaultImports</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>module === &quot;system&quot;</code>或设置了<code>--esModuleInterop</code></td><td style="text-align:left;">允许从没有设置默认导出的模块中默认导入。这并不影响代码的输出，仅为了类型检查。</td></tr><tr><td style="text-align:left;"><code>--allowUnreachableCode</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不报告执行不到的代码错误。</td></tr><tr><td style="text-align:left;"><code>--allowUnusedLabels</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不报告未使用的标签错误。</td></tr><tr><td style="text-align:left;"><code>--alwaysStrict</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">以严格模式解析并为每个源文件生成<code>&quot;use strict&quot;</code>语句</td></tr><tr><td style="text-align:left;"><code>--baseUrl</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">解析非相对模块名的基准目录。查看<a href="./../handbook/module-resolution#base-url">模块解析文档</a>了解详情。</td></tr><tr><td style="text-align:left;"><code>--build</code> <code>-b</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">使用<a href="./project-references">Project References</a>来构建此工程及其依赖工程。注意这个标记与本页内其它标记不兼容。详情参考<a href="./project-references">这里</a></td></tr><tr><td style="text-align:left;"><code>--charset</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>&quot;utf8&quot;</code></td><td style="text-align:left;">输入文件的字符集。</td></tr><tr><td style="text-align:left;"><code>--checkJs</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">在.js文件中报告错误。与<code>--allowJs</code>配合使用。</td></tr><tr><td style="text-align:left;"><code>--composite</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>true</code></td><td style="text-align:left;">确保TypeScript能够找到编译当前工程所需要的引用工程的输出位置。</td></tr><tr><td style="text-align:left;"><code>--declaration</code> <code>-d</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">生成相应的<code>.d.ts</code>文件。</td></tr><tr><td style="text-align:left;"><code>--declarationDir</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">生成声明文件的输出路径。</td></tr><tr><td style="text-align:left;"><code>--diagnostics</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">显示诊断信息。</td></tr><tr><td style="text-align:left;"><code>--disableSizeLimit</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">禁用JavaScript工程体积大小的限制</td></tr><tr><td style="text-align:left;"><code>--emitBOM</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">在输出文件的开头加入BOM头（UTF-8 Byte Order Mark）。</td></tr><tr><td style="text-align:left;"><code>--emitDecoratorMetadata</code>[1]</td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">给源码里的装饰器声明加上设计类型元数据。查看<a href="https://github.com/Microsoft/TypeScript/issues/2577" target="_blank" rel="noreferrer">issue #2577</a>了解更多信息。</td></tr><tr><td style="text-align:left;"><code>--experimentalDecorators</code>[1]</td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">启用实验性的ES装饰器。</td></tr><tr><td style="text-align:left;"><code>--extendedDiagnostics</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">显示详细的诊段信息。</td></tr><tr><td style="text-align:left;"><code>--forceConsistentCasingInFileNames</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">禁止对同一个文件的不一致的引用。</td></tr><tr><td style="text-align:left;"><code>--generateCpuProfile</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>profile.cpuprofile</code></td><td style="text-align:left;">在指定目录生成CPU资源使用报告。若传入的是已创建的目录名，将在此目录下生成以时间戳命名的报告。</td></tr><tr><td style="text-align:left;"><code>--help</code> <code>-h</code></td><td style="text-align:left;"></td><td style="text-align:left;"></td><td style="text-align:left;">打印帮助信息。</td></tr><tr><td style="text-align:left;"><code>--importHelpers</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">从<a href="https://www.npmjs.com/package/tslib" target="_blank" rel="noreferrer"><code>tslib</code></a>导入辅助工具函数（比如<code>__extends</code>，<code>__rest</code>等）</td></tr><tr><td style="text-align:left;"><code>--importsNotUsedAsValues</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>remove</code></td><td style="text-align:left;">用于设置针对于类型导入的代码生成和代码检查的行为。<code>&quot;remove&quot;</code>和<code>&quot;preserve&quot;</code>设置了是否对未使用的导入了模块副作用的导入语句生成相关代码，<code>&quot;error&quot;</code>则强制要求只用作类型的模块导入必须使用<code>import type</code>语句。</td></tr><tr><td style="text-align:left;"><code>--inlineSourceMap</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">生成单个sourcemaps文件，而不是将每sourcemaps生成不同的文件。</td></tr><tr><td style="text-align:left;"><code>--inlineSources</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">将代码与sourcemaps生成到一个文件中，要求同时设置了<code>--inlineSourceMap</code>或<code>--sourceMap</code>属性。</td></tr><tr><td style="text-align:left;"><code>--init</code></td><td style="text-align:left;"></td><td style="text-align:left;"></td><td style="text-align:left;">初始化TypeScript项目并创建一个<code>tsconfig.json</code>文件。</td></tr><tr><td style="text-align:left;"><code>--isolatedModules</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">执行额外检查以确保单独编译（如<a href="https://github.com/Microsoft/TypeScript/wiki/Using-the-Compiler-API#a-simple-transform-function" target="_blank" rel="noreferrer"><code>transpileModule</code></a>或<a href="https://babeljs.io/docs/en/babel-plugin-transform-typescript" target="_blank" rel="noreferrer">@babel/plugin-transform-typescript</a>）是安全的。</td></tr><tr><td style="text-align:left;"><code>--jsx</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>&quot;preserve&quot;</code></td><td style="text-align:left;">在<code>.tsx</code>文件里支持JSX：<code>&quot;react&quot;</code>或<code>&quot;preserve&quot;</code>或<code>&quot;react-native&quot;</code>。查看<a href="./../handbook/jsx">JSX</a>。</td></tr><tr><td style="text-align:left;"><code>--jsxFactory</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>&quot;React.createElement&quot;</code></td><td style="text-align:left;">指定生成目标为react JSX时，使用的JSX工厂函数，比如<code>React.createElement</code>或<code>h</code>。</td></tr><tr><td style="text-align:left;"><code>--lib</code></td><td style="text-align:left;"><code>string[]</code></td><td style="text-align:left;"></td><td style="text-align:left;">编译过程中需要引入的库文件的列表。 可能的值为： ► <code>ES5</code> ► <code>ES6</code> ► <code>ES2015</code> ► <code>ES7</code> ► <code>ES2016</code> ► <code>ES2017</code> ► <code>ES2018</code> ► <code>ESNext</code> ► <code>DOM</code> ► <code>DOM.Iterable</code> ► <code>WebWorker</code> ► <code>ScriptHost</code> ► <code>ES2015.Core</code> ► <code>ES2015.Collection</code> ► <code>ES2015.Generator</code> ► <code>ES2015.Iterable</code> ► <code>ES2015.Promise</code> ► <code>ES2015.Proxy</code> ► <code>ES2015.Reflect</code> ► <code>ES2015.Symbol</code> ► <code>ES2015.Symbol.WellKnown</code> ► <code>ES2016.Array.Include</code> ► <code>ES2017.object</code> ► <code>ES2017.Intl</code> ► <code>ES2017.SharedMemory</code> ► <code>ES2017.String</code> ► <code>ES2017.TypedArrays</code> ► <code>ES2018.Intl</code> ► <code>ES2018.Promise</code> ► <code>ES2018.RegExp</code> ► <code>ESNext.AsyncIterable</code> ► <code>ESNext.Array</code> ► <code>ESNext.Intl</code> ► <code>ESNext.Symbol</code> 注意：如果<code>--lib</code>没有指定默认注入的库的列表。默认注入的库为： ► 针对于<code>--target ES5</code>：<code>DOM，ES5，ScriptHost</code> ► 针对于<code>--target ES6</code>：<code>DOM，ES6，DOM.Iterable，ScriptHost</code></td></tr><tr><td style="text-align:left;"><code>--listEmittedFiles</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">打印出编译后生成文件的名字。</td></tr><tr><td style="text-align:left;"><code>--listFiles</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">编译过程中打印文件名。</td></tr><tr><td style="text-align:left;"><code>--locale</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><em>(platform specific)</em></td><td style="text-align:left;">显示错误信息时使用的语言，比如：en-us。</td></tr><tr><td style="text-align:left;"><code>--mapRoot</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">为调试器指定指定sourcemap文件的路径，而不是使用生成时的路径。当<code>.map</code>文件是在运行时指定的，并不同于<code>js</code>文件的地址时使用这个标记。指定的路径会嵌入到<code>sourceMap</code>里告诉调试器到哪里去找它们。使用此标识并不会新创建指定目录并生成map文件在指定路径下。而是增加一个构建后的步骤，把相应文件移动到指定路径下。</td></tr><tr><td style="text-align:left;"><code>--maxNodeModuleJsDepth</code></td><td style="text-align:left;"><code>number</code></td><td style="text-align:left;"><code>0</code></td><td style="text-align:left;">node_modules依赖的最大搜索深度并加载JavaScript文件。仅适用于<code>--allowJs</code>。</td></tr><tr><td style="text-align:left;"><code>--module</code> <code>-m</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>target === &quot;ES6&quot; ? &quot;ES6&quot; : &quot;commonjs&quot;</code></td><td style="text-align:left;">指定生成哪个模块系统代码：<code>&quot;None&quot;</code>，<code>&quot;CommonJS&quot;</code>，<code>&quot;AMD&quot;</code>，<code>&quot;System&quot;</code>，<code>&quot;UMD&quot;</code>，<code>&quot;ES6&quot;</code>或<code>&quot;ES2015&quot;</code>。 ► 只有<code>&quot;AMD&quot;</code>和<code>&quot;System&quot;</code>能和<code>--outFile</code>一起使用。 ►<code>&quot;ES6&quot;</code>和<code>&quot;ES2015&quot;</code>可使用在目标输出为<code>&quot;ES5&quot;</code>或更低的情况下。</td></tr><tr><td style="text-align:left;"><code>--moduleResolution</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>module === &quot;AMD&quot; or &quot;System&quot; or &quot;ES6&quot; ? &quot;Classic&quot; : &quot;Node&quot;</code></td><td style="text-align:left;">决定如何处理模块。或者是<code>&quot;Node&quot;</code>对于Node.js/io.js，或者是<code>&quot;Classic&quot;</code>（默认）。查看<a href="./../handbook/module-resolution">模块解析</a>了解详情。</td></tr><tr><td style="text-align:left;"><code>--newLine</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><em>(platform specific)</em></td><td style="text-align:left;">当生成文件时指定行结束符：<code>&quot;crlf&quot;</code>（windows）或<code>&quot;lf&quot;</code>（unix）。</td></tr><tr><td style="text-align:left;"><code>--noEmit</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不生成输出文件。</td></tr><tr><td style="text-align:left;"><code>--noEmitHelpers</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不在输出文件中生成用户自定义的帮助函数代码，如<code>__extends</code>。</td></tr><tr><td style="text-align:left;"><code>--noEmitOnError</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">报错时不生成输出文件。</td></tr><tr><td style="text-align:left;"><code>--noErrorTruncation</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不截短错误消息。</td></tr><tr><td style="text-align:left;"><code>--noFallthroughCasesInSwitch</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">报告switch语句的fallthrough错误。（即，不允许switch的case语句贯穿）</td></tr><tr><td style="text-align:left;"><code>--noImplicitAny</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">在表达式和声明上有隐含的<code>any</code>类型时报错。</td></tr><tr><td style="text-align:left;"><code>--noImplicitReturns</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不是函数的所有返回路径都有返回值时报错。</td></tr><tr><td style="text-align:left;"><code>--noImplicitThis</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">当<code>this</code>表达式的值为<code>any</code>类型的时候，生成一个错误。</td></tr><tr><td style="text-align:left;"><code>--noImplicitUseStrict</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">模块输出中不包含<code>&quot;use strict&quot;</code>指令。</td></tr><tr><td style="text-align:left;"><code>--noLib</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不包含默认的库文件（<code>lib.d.ts</code>）。</td></tr><tr><td style="text-align:left;"><code>--noResolve</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不把<code>/// &lt;reference``&gt;</code>或模块导入的文件加到编译文件列表。</td></tr><tr><td style="text-align:left;"><code>--noStrictGenericChecks</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">禁用在函数类型里对泛型签名进行严格检查。</td></tr><tr><td style="text-align:left;"><code>--noUnusedLocals</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">若有未使用的局部变量则抛错。</td></tr><tr><td style="text-align:left;"><code>--noUnusedParameters</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">若有未使用的参数则抛错。</td></tr><tr><td style="text-align:left;"><s><code>--out</code></s></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">弃用。使用 <code>--outFile</code> 代替。</td></tr><tr><td style="text-align:left;"><code>--outDir</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">重定向输出目录。</td></tr><tr><td style="text-align:left;"><code>--outFile</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">将输出文件合并为一个文件。合并的顺序是根据传入编译器的文件顺序和<code>///&lt;reference``&gt;</code>和<code>import</code>的文件顺序决定的。查看输出文件顺序文档<a href="https://github.com/Microsoft/TypeScript/wiki/FAQ#how-do-i-control-file-ordering-in-combined-output---out-" target="_blank" rel="noreferrer">了解详情</a>。</td></tr><tr><td style="text-align:left;"><code>paths</code>[2]</td><td style="text-align:left;"><code>Object</code></td><td style="text-align:left;"></td><td style="text-align:left;">模块名到基于<code>baseUrl</code>的路径映射的列表。查看<a href="./../handbook/module-resolution#path-mapping">模块解析文档</a>了解详情。</td></tr><tr><td style="text-align:left;"><code>--preserveConstEnums</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">保留<code>const</code>和<code>enum</code>声明。查看<a href="https://github.com/Microsoft/TypeScript/blob/master/doc/spec.md#94-constant-enum-declarations" target="_blank" rel="noreferrer">const enums documentation</a>了解详情。</td></tr><tr><td style="text-align:left;"><code>--preserveSymlinks</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不把符号链接解析为其真实路径；将符号链接文件视为真正的文件。</td></tr><tr><td style="text-align:left;"><code>--preserveWatchOutput</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">保留watch模式下过时的控制台输出。</td></tr><tr><td style="text-align:left;"><code>--pretty</code>[1]</td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">给错误和消息设置样式，使用颜色和上下文。</td></tr><tr><td style="text-align:left;"><code>--project</code> <code>-p</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">编译指定目录下的项目。这个目录应该包含一个<code>tsconfig.json</code>文件来管理编译。查看<a href="./tsconfig.json">tsconfig.json</a>文档了解更多信息。</td></tr><tr><td style="text-align:left;"><code>--reactNamespace</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>&quot;React&quot;</code></td><td style="text-align:left;">当目标为生成<code>&quot;react&quot;</code> JSX时，指定<code>createElement</code>和<code>__spread</code>的调用对象</td></tr><tr><td style="text-align:left;"><code>--removeComments</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">删除所有注释，除了以<code>/!*</code>开头的版权信息。</td></tr><tr><td style="text-align:left;"><code>--rootDir</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><em>(common root directory is computed from the list of input files)</em></td><td style="text-align:left;">仅用来控制输出的目录结构<code>--outDir</code>。</td></tr><tr><td style="text-align:left;"><code>rootDirs</code>[2]</td><td style="text-align:left;"><code>string[]</code></td><td style="text-align:left;"></td><td style="text-align:left;">根（root）文件夹列表，表示运行时组合工程结构的内容。查看<a href="./../handbook/module-resolution#virtual-directories-with-rootdirs">模块解析文档</a>了解详情。</td></tr><tr><td style="text-align:left;"><code>--showConfig</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">不真正执行build，而是显示build使用的配置文件信息。</td></tr><tr><td style="text-align:left;"><code>--skipDefaultLibCheck</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">忽略<a href="./../handbook/triple-slash-directives#-reference-no-default-libtrue">库的默认声明文件</a>的类型检查。</td></tr><tr><td style="text-align:left;"><code>--skipLibCheck</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">忽略所有的声明文件（<code>*.d.ts</code>）的类型检查。</td></tr><tr><td style="text-align:left;"><code>--sourceMap</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">生成相应的<code>.map</code>文件。</td></tr><tr><td style="text-align:left;"><code>--sourceRoot</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"></td><td style="text-align:left;">指定TypeScript源文件的路径，以便调试器定位。当TypeScript文件的位置是在运行时指定时使用此标记。路径信息会被加到<code>sourceMap</code>里。</td></tr><tr><td style="text-align:left;"><code>--strict</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">启用所有严格检查选项。 包含<code>--noImplicitAny</code>, <code>--noImplicitThis</code>, <code>--alwaysStrict</code>, <code>--strictBindCallApply</code>, <code>--strictNullChecks</code>, <code>--strictFunctionTypes</code>和<code>--strictPropertyInitialization</code>.</td></tr><tr><td style="text-align:left;"><code>--strictFunctionTypes</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">禁用函数参数双向协变检查。</td></tr><tr><td style="text-align:left;"><code>--strictPropertyInitialization</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">确保类的非<code>undefined</code>属性已经在构造函数里初始化。若要令此选项生效，需要同时启用<code>--strictNullChecks</code>。</td></tr><tr><td style="text-align:left;"><code>--strictNullChecks</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">在严格的<code>null</code>检查模式下，<code>null</code>和<code>undefined</code>值不包含在任何类型里，只允许用它们自己和<code>any</code>来赋值（有个例外，<code>undefined</code>可以赋值到<code>void</code>）。</td></tr><tr><td style="text-align:left;"><code>--suppressExcessPropertyErrors</code>[1]</td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">阻止对对象字面量的额外属性检查。</td></tr><tr><td style="text-align:left;"><code>--suppressImplicitAnyIndexErrors</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">阻止<code>--noImplicitAny</code>对缺少索引签名的索引对象报错。查看<a href="https://github.com/Microsoft/TypeScript/issues/1232#issuecomment-64510362" target="_blank" rel="noreferrer">issue #1232</a>了解详情。</td></tr><tr><td style="text-align:left;"><code>--target</code> <code>-t</code></td><td style="text-align:left;"><code>string</code></td><td style="text-align:left;"><code>&quot;ES3&quot;</code></td><td style="text-align:left;">指定ECMAScript目标版本<code>&quot;ES3&quot;</code>（默认），<code>&quot;ES5&quot;</code>，<code>&quot;ES6&quot;</code>/<code>&quot;ES2015&quot;</code>，<code>&quot;ES2016&quot;</code>，<code>&quot;ES2017&quot;</code>，<code>&quot;ES2018&quot;</code>，<code>&quot;ES2019&quot;</code>，<code>&quot;ES2020&quot;</code>或<code>&quot;ESNext&quot;</code>。 注意：<code>&quot;ESNext&quot;</code>最新的生成目标列表为<a href="https://github.com/tc39/proposals" target="_blank" rel="noreferrer">ES proposed features</a></td></tr><tr><td style="text-align:left;"><code>--traceResolution</code></td><td style="text-align:left;"><code>boolean</code></td><td style="text-align:left;"><code>false</code></td><td style="text-align:left;">生成模块解析日志信息</td></tr><tr><td style="text-align:left;"><code>--types</code></td><td style="text-align:left;"><code>string[]</code></td><td style="text-align:left;"></td><td style="text-align:left;">要包含的类型声明文件名列表。查看<a href="./tsconfig.json#types-typeroots-and-types">@types，--typeRoots和--types</a>章节了解详细信息。</td></tr><tr><td style="text-align:left;"><code>--typeRoots</code></td><td style="text-align:left;"><code>string[]</code></td><td style="text-align:left;"></td><td style="text-align:left;">要包含的类型声明文件路径列表。查看<a href="./tsconfig.json#types-typeroots-and-types">@types，--typeRoots和--types</a>章节了解详细信息。</td></tr><tr><td style="text-align:left;"><code>--version</code> <code>-v</code></td><td style="text-align:left;"></td><td style="text-align:left;"></td><td style="text-align:left;">打印编译器版本号。</td></tr><tr><td style="text-align:left;"><code>--watch</code> <code>-w</code></td><td style="text-align:left;"></td><td style="text-align:left;"></td><td style="text-align:left;">在监视模式下运行编译器。会监视输出文件，在它们改变时重新编译。监视文件和目录的具体实现可以通过环境变量进行配置。详情请看<a href="./configuring-watch">配置 Watch</a>。</td></tr></tbody></table><ul><li>[1] 这些选项是试验性的。</li><li>[2] 这些选项只能在<code>tsconfig.json</code>里使用，不能在命令行使用。</li></ul><h2 id="相关信息" tabindex="-1">相关信息 <a class="header-anchor" href="#相关信息" aria-label="Permalink to &quot;相关信息&quot;">​</a></h2><ul><li>在<a href="./tsconfig.json"><code>tsconfig.json</code></a>文件里设置编译器选项。</li><li>在<a href="./compiler-options-in-msbuild">MSBuild工程</a>里设置编译器选项。</li></ul>',6),a=[c];function s(n,i,r,f,g,y){return e(),d("div",null,a)}const p=t(o,[["render",s]]);export{u as __pageData,p as default};
